import { useSelector, useDispatch } from "react-redux";
import "./App.css";
import { store } from "./ReduxStore";

function App() {
  // console.log(store);
  const { countObj, inputTextObj } = store.getState();
  console.log(countObj);

  const count = countObj.count;
  const darkMode = countObj.darkMode;
  // const count = useSelector((state) => state.count);
  // const darkMode = useSelector((state) => state.darkMode);
  const dispatch = useDispatch();
  // console.log(dispatch);

  return (
    <div className={darkMode === false ? "App" : "dark-mode"}>
      <h1>Redux Practice</h1>
      <p>{count}</p>
      <div>
        <button
          onClick={() => {
            dispatch({ type: "decrease" });
          }}
        >
          Decrease
        </button>
        <button
          onClick={() => {
            dispatch({ type: "reset" });
          }}
        >
          Reset
        </button>
        <button
          onClick={() => {
            // dispatch({ type: "increase" });
            dispatch({
              type: "change",
              payload: { inputTextObj: { inputText: "Hello World" } },
            });
          }}
        >
          Increase
        </button>
      </div>
      <button
        onClick={() => {
          dispatch({ type: "toggle" });
        }}
      >
        Dark Mode
      </button>
    </div>
  );
}

export default App;
