import { useDispatch, useSelector } from "react-redux";
import "./App.css";

function App() {
  const count = useSelector((state) => state.countObj.count);
  const darkMode = useSelector((state) => state.countObj.darkMode);
  const inputText = useSelector((state) => state.inputTextObj.inputText);

  const dispatch = useDispatch();

  return (
    <div className={darkMode === false ? "App" : "dark-mode"}>
      <div>
        <h1>Redux Practice</h1>
        <p>{count}</p>
        <div>
          <button
            onClick={() => {
              dispatch({ type: "decrease" });
            }}
          >
            Decrease
          </button>
          <button
            onClick={() => {
              dispatch({ type: "reset" });
            }}
          >
            Reset
          </button>
          <button
            onClick={() => {
              dispatch({ type: "increase" });
            }}
          >
            Increase
          </button>
        </div>
        <button
          onClick={() => {
            dispatch({ type: "toggle" });
          }}
        >
          Dark Mode
        </button>
      </div>
      <div>
        <p>{inputText ? inputText : "hello, type something"}</p>
        <input
          type="text"
          onChange={(e) => {
            dispatch({ type: "change", payload: e.target.value });
          }}
        />
      </div>
    </div>
  );
}

export default App;
